                if verbose:
                    tqdm.write( even_print('read vort_x', '%0.3f [GB]  %0.3f [s]  %0.3f [GB/s]'%(data_gb,t_delta,(data_gb/t_delta)), s=True ) )
                
                dset = self['data/vort_y']
                if self.usingmpi: self.comm.Barrier()
