        self.create_dataset('dims/x', data=x, chunks=None)
        self.create_dataset('dims/y', data=y, chunks=None)
        self.create_dataset('dims/z', data=z, chunks=None)
        self.create_dataset('dims/t', data=t, chunks=None)
        
