        #self.scalars = ['u','v','w']
        self.scalars = ['u']
        self.scalars_dtypes = [np.float32 for s in self.scalars]
        
        ## initialize datasets
        data_gb = 4*nx*ny*nz*nt / 1024.**3
        if self.usingmpi: self.comm.Barrier()
